package com.java.Practice;

public class even {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.print("Even numbers= ");
		for(int i=0; i<=10; i++) {
			
			if(i%2==0) {
				
				System.out.print(i+" ");
			}
		}
		
			System.out.println("");
			System.out.print("Odd Numbers= ");
			for(int j=0; j<=10; j++) {
				
				if(j%2!=0) {
					
					System.out.print(j+" ");
		}
			}
			
	}

}
